import React, { useEffect, useState } from "react";

export default function Student() {
  const [reports, setReports] = useState([]);
  const [msg, setMsg] = useState("");
  const [rating, setRating] = useState({
    reportId: "",
    rating: 5,
    feedback: "",
    studentId: 1, // hardcode for now; later replace with logged-in user
  });

  // Load reports
  useEffect(() => {
    fetch("http://localhost:5000/api/reports")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setReports(data);
        else setReports([]);
      })
      .catch((err) => {
        console.error(err);
        setMsg("❌ Failed to load reports");
      });
  }, []);

  // Handle form change
  const handleChange = (e) =>
    setRating({ ...rating, [e.target.name]: e.target.value });

  // Submit rating
  const submitRating = async (e) => {
    e.preventDefault();
    if (!rating.reportId) {
      setMsg("❌ Please select a report before submitting.");
      return;
    }
    try {
      const res = await fetch("http://localhost:5000/api/ratings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(rating),
      });
      if (res.ok) {
        setMsg("✅ Rating submitted successfully!");
        setRating({ reportId: "", rating: 5, feedback: "", studentId: 1 });
      } else {
        const errMsg = await res.json();
        setMsg("❌ Failed: " + (errMsg.error || "Unknown error"));
      }
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  return (
    <div>
      <h2>Student Dashboard</h2>
      {msg && <p>{msg}</p>}

      <h3>Available Reports</h3>
      {reports.length === 0 ? (
        <p>No reports found</p>
      ) : (
        <ul>
          {reports.map((r) => (
            <li key={r.id}>
              {r.course_name} - {r.topic_taught} ({r.date_of_lecture})
            </li>
          ))}
        </ul>
      )}

      <h3>Submit Rating</h3>
      <form onSubmit={submitRating}>
        <select
          name="reportId"
          value={rating.reportId}
          onChange={handleChange}
          className="form-select mb-2"
        >
          <option value="">Select Report</option>
          {reports.map((r) => (
            <option key={r.id} value={r.id}>
              {r.course_name} - {r.topic_taught}
            </option>
          ))}
        </select>

        <input
          type="number"
          name="rating"
          min="1"
          max="5"
          value={rating.rating}
          onChange={handleChange}
          className="form-control mb-2"
        />

        <textarea
          name="feedback"
          placeholder="Enter feedback"
          value={rating.feedback}
          onChange={handleChange}
          className="form-control mb-2"
        />

        <button className="btn btn-primary">Submit Rating</button>
      </form>
    </div>
  );
}
