import React, { useEffect, useState } from "react";

export default function PRL() {
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [msg, setMsg] = useState("");

  useEffect(() => {
    fetch("/api/reports")
      .then((res) => res.json())
      .then(setReports)
      .catch(console.error);
  }, []);

  const handleChange = (id, value) => {
    setFeedback({ ...feedback, [id]: value });
  };

  const submitFeedback = async (id) => {
    try {
      const res = await fetch(`/api/reports/${id}/feedback`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ feedback: feedback[id] }),
      });
      if (res.ok) setMsg("✅ Feedback saved");
      else setMsg("❌ Failed to save feedback");
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  return (
    <div>
      <h2>PRL Dashboard</h2>
      {msg && <p>{msg}</p>}
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Date</th>
            <th>Course</th>
            <th>Lecturer</th>
            <th>Topic</th>
            <th>Feedback</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((r) => (
            <tr key={r.id}>
              <td>{r.date_of_lecture}</td>
              <td>{r.course_name}</td>
              <td>{r.lecturer_name}</td>
              <td>{r.topic_taught}</td>
              <td>
                <textarea
                  className="form-control mb-2"
                  value={feedback[r.id] || ""}
                  onChange={(e) => handleChange(r.id, e.target.value)}
                />
                <button
                  className="btn btn-sm btn-primary"
                  onClick={() => submitFeedback(r.id)}
                >
                  Save
                </button>
                {r.prl_feedback && (
                  <p className="mt-1 text-muted">Existing: {r.prl_feedback}</p>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
