import React, { useState } from "react";
import axios from "axios";

export default function Login({ setUser }) {
  const [form, setForm] = useState({ username: "", password: "" });
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg("");
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5001/login", form);

      const user = res.data; // { username, role }
      localStorage.setItem("user", JSON.stringify(user));
      setUser(user);

      setMsg(`✅ Login successful! Logged in as ${user.role}`);
      setLoading(false);

      // Optional: redirect based on role
      // if (user.role === "student") navigate("/student");
      // if (user.role === "lecturer") navigate("/report");

    } catch (err) {
      console.error(err);
      if (err.response) {
        setMsg(err.response.data.message || "❌ Login failed");
      } else {
        setMsg("❌ Server unreachable");
      }
      setLoading(false);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4 shadow" style={{ minWidth: "350px" }}>
        <h2 className="card-title text-center mb-3">Login</h2>
        {msg && <div className="alert alert-info">{msg}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Username</label>
            <input
              type="text"
              name="username"
              value={form.username}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              value={form.password}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary w-100" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
      </div>
    </div>
  );
}
