import React, { useEffect, useState } from "react";

export default function PL() {
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [form, setForm] = useState({ name: "", code: "", lecturerId: "" });
  const [editingCourse, setEditingCourse] = useState(null);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    // Load courses
    fetch("http://localhost:5000/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(Array.isArray(data) ? data : []))
      .catch(() => setMsg("❌ Failed to load courses"));

    // Load reports
    fetch("http://localhost:5000/api/reports")
      .then((res) => res.json())
      .then((data) => setReports(Array.isArray(data) ? data : []))
      .catch(() => setMsg("❌ Failed to load reports"));

    // Load lecturers (users with role=lecturer)
    fetch("http://localhost:5000/api/users?role=lecturer")
      .then((res) => res.json())
      .then((data) => setLecturers(Array.isArray(data) ? data : []))
      .catch(() => setMsg("❌ Failed to load lecturers"));
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const addCourse = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (res.ok) {
        const newCourse = await res.json();
        setMsg("✅ Course added");
        setCourses([...courses, newCourse]);
        setForm({ name: "", code: "", lecturerId: "" });
      } else {
        setMsg("❌ Failed to add course");
      }
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  const updateCourse = async (courseId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/courses/${courseId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editingCourse),
      });
      if (res.ok) {
        setMsg("✅ Course updated");
        setCourses(
          courses.map((c) => (c.id === courseId ? { ...c, ...editingCourse } : c))
        );
        setEditingCourse(null);
      } else {
        setMsg("❌ Failed to update course");
      }
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  return (
    <div>
      <h2 className="mb-4">📘 Program Leader (PL) Dashboard</h2>
      {msg && <div className="alert alert-info">{msg}</div>}

      {/* Add Course Form */}
      <div className="card shadow-sm mb-4">
        <div className="card-header bg-dark text-white">
          <h5 className="mb-0">➕ Add New Course</h5>
        </div>
        <div className="card-body">
          <form onSubmit={addCourse}>
            <div className="mb-3">
              <label className="form-label">Course Name</label>
              <input
                name="name"
                value={form.name}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Course Code</label>
              <input
                name="code"
                value={form.code}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Assign Lecturer</label>
              <select
                name="lecturerId"
                className="form-select"
                value={form.lecturerId}
                onChange={handleChange}
              >
                <option value="">-- Select Lecturer --</option>
                {lecturers.map((lec) => (
                  <option key={lec.id} value={lec.id}>
                    {lec.username}
                  </option>
                ))}
              </select>
            </div>
            <button className="btn btn-primary">Add Course</button>
          </form>
        </div>
      </div>

      {/* Courses List */}
      <h3 className="mb-3">📚 Courses</h3>
      {courses.length === 0 ? (
        <p>No courses available</p>
      ) : (
        <div className="row">
          {courses.map((c) => (
            <div key={c.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  {editingCourse?.id === c.id ? (
                    <>
                      <h5 className="card-title">
                        <input
                          name="name"
                          value={editingCourse.name}
                          onChange={(e) =>
                            setEditingCourse({
                              ...editingCourse,
                              [e.target.name]: e.target.value,
                            })
                          }
                          className="form-control"
                        />
                      </h5>
                      <h6 className="card-subtitle mb-2 text-muted">
                        <input
                          name="code"
                          value={editingCourse.code}
                          onChange={(e) =>
                            setEditingCourse({
                              ...editingCourse,
                              [e.target.name]: e.target.value,
                            })
                          }
                          className="form-control"
                        />
                      </h6>
                      <select
                        name="lecturerId"
                        className="form-select mb-2"
                        value={editingCourse.lecturerId}
                        onChange={(e) =>
                          setEditingCourse({
                            ...editingCourse,
                            lecturerId: e.target.value,
                          })
                        }
                      >
                        <option value="">-- Select Lecturer --</option>
                        {lecturers.map((lec) => (
                          <option key={lec.id} value={lec.id}>
                            {lec.username}
                          </option>
                        ))}
                      </select>
                      <button
                        className="btn btn-success btn-sm me-2"
                        onClick={() => updateCourse(c.id)}
                      >
                        Save
                      </button>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => setEditingCourse(null)}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <h5 className="card-title">{c.name}</h5>
                      <h6 className="card-subtitle mb-2 text-muted">{c.code}</h6>
                      <p className="card-text">
                        Lecturer:{" "}
                        {lecturers.find((lec) => lec.id === c.lecturer_id)?.username ||
                          "Unassigned"}
                      </p>
                      <button
                        className="btn btn-outline-primary btn-sm"
                        onClick={() => setEditingCourse(c)}
                      >
                        Edit
                      </button>
                    </>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Reports Overview */}
      <h3 className="mb-3">📋 Reports Overview</h3>
      {reports.length === 0 ? (
        <p>No reports available</p>
      ) : (
        <div className="row">
          {reports.map((r) => (
            <div key={r.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">{r.course_name}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">
                    {r.course_code} | {r.date_of_lecture}
                  </h6>
                  <p className="card-text">
                    <strong>Topic:</strong> {r.topic_taught} <br />
                    <strong>Lecturer:</strong> {r.lecturer_name}
                  </p>
                </div>
                <div className="card-footer text-end">
                  <small className="text-muted">
                    Week {r.week_of_reporting}
                  </small>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
