import React, { useEffect, useState } from "react";

export default function PL() {
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [form, setForm] = useState({ name: "", code: "", lecturerId: "" });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    fetch("/api/courses")
      .then((res) => res.json())
      .then(setCourses);

    fetch("/api/reports")
      .then((res) => res.json())
      .then(setReports);
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const addCourse = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("/api/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (res.ok) setMsg("✅ Course added");
      else setMsg("❌ Failed to add course");
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  return (
    <div>
      <h2>Program Leader (PL) Dashboard</h2>
      {msg && <p>{msg}</p>}

      <h3>Add Course</h3>
      <form onSubmit={addCourse} className="mb-3">
        <input
          name="name"
          placeholder="Course Name"
          className="form-control mb-2"
          onChange={handleChange}
        />
        <input
          name="code"
          placeholder="Course Code"
          className="form-control mb-2"
          onChange={handleChange}
        />
        <input
          name="lecturerId"
          placeholder="Lecturer ID (optional)"
          className="form-control mb-2"
          onChange={handleChange}
        />
        <button className="btn btn-primary">Add Course</button>
      </form>

      <h3>Courses</h3>
      <ul>
        {courses.map((c) => (
          <li key={c.id}>
            {c.name} ({c.code})
          </li>
        ))}
      </ul>

      <h3>Reports Overview</h3>
      <ul>
        {reports.map((r) => (
          <li key={r.id}>
            {r.course_name} - {r.topic_taught}
          </li>
        ))}
      </ul>
    </div>
  );
}
