import React, { useEffect, useState } from "react";

export default function PL() {
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [form, setForm] = useState({ name: "", code: "", lecturerId: "" });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    // Load courses
    fetch("http://localhost:5000/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(Array.isArray(data) ? data : []))
      .catch(() => setMsg("‚ùå Failed to load courses"));

    // Load reports
    fetch("http://localhost:5000/api/reports")
      .then((res) => res.json())
      .then((data) => setReports(Array.isArray(data) ? data : []))
      .catch(() => setMsg("‚ùå Failed to load reports"));

    // Load lecturers (users with role=lecturer)
    fetch("http://localhost:5000/api/users?role=lecturer")
      .then((res) => res.json())
      .then((data) => setLecturers(Array.isArray(data) ? data : []))
      .catch(() => setMsg("‚ùå Failed to load lecturers"));
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const addCourse = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (res.ok) {
        setMsg("‚úÖ Course added");
        setCourses([...courses, form]); // optimistic update
        setForm({ name: "", code: "", lecturerId: "" });
      } else {
        setMsg("‚ùå Failed to add course");
      }
    } catch (err) {
      setMsg("‚ùå Error: " + err.message);
    }
  };

  return (
    <div>
      <h2 className="mb-4">üìò Program Leader (PL) Dashboard</h2>
      {msg && <div className="alert alert-info">{msg}</div>}

      {/* Add Course Form */}
      <div className="card shadow-sm mb-4">
        <div className="card-header bg-dark text-white">
          <h5 className="mb-0">‚ûï Add New Course</h5>
        </div>
        <div className="card-body">
          <form onSubmit={addCourse}>
            <div className="mb-3">
              <label className="form-label">Course Name</label>
              <input
                name="name"
                placeholder="Course Name"
                className="form-control"
                value={form.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Course Code</label>
              <input
                name="code"
                placeholder="Course Code"
                className="form-control"
                value={form.code}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Assign Lecturer</label>
              <select
                name="lecturerId"
                className="form-select"
                value={form.lecturerId}
                onChange={handleChange}
              >
                <option value="">-- Select Lecturer --</option>
                {lecturers.map((lec) => (
                  <option key={lec.id} value={lec.id}>
                    {lec.username} ({lec.id})
                  </option>
                ))}
              </select>
            </div>
            <button className="btn btn-primary">Add Course</button>
          </form>
        </div>
      </div>

      {/* Courses List */}
      <h3 className="mb-3">üìö Courses</h3>
      {courses.length === 0 ? (
        <p>No courses available</p>
      ) : (
        <div className="row">
          {courses.map((c, i) => (
            <div key={i} className="col-md-6 col-lg-4 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">{c.name}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">{c.code}</h6>
                  <p className="card-text">
                    Lecturer:{" "}
                    {lecturers.find((lec) => lec.id === c.lecturer_id)?.username ||
                      "Unassigned"}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Reports Overview */}
      <h3 className="mb-3">üìã Reports Overview</h3>
      {reports.length === 0 ? (
        <p>No reports available</p>
      ) : (
        <div className="row">
          {reports.map((r) => (
            <div key={r.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">{r.course_name}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">
                    {r.course_code} | {r.date_of_lecture}
                  </h6>
                  <p className="card-text">
                    <strong>Topic:</strong> {r.topic_taught} <br />
                    <strong>Lecturer:</strong> {r.lecturer_name}
                  </p>
                </div>
                <div className="card-footer text-end">
                  <small className="text-muted">
                    Week {r.week_of_reporting}
                  </small>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
