import React, { useEffect, useState } from "react";

export default function Student() {
  const [reports, setReports] = useState([]);
  const [rating, setRating] = useState({ reportId: "", rating: 5, feedback: "" });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/reports")
      .then(res => res.json())
      .then(setReports)
      .catch(console.error);
  }, []);

  const handleChange = e =>
    setRating({ ...rating, [e.target.name]: e.target.value });

  const submitRating = async e => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/ratings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(rating),
      });
      if (res.ok) setMsg("✅ Rating submitted!");
      else setMsg("❌ Failed to submit rating");
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  return (
    <div>
      <h2>Student Dashboard</h2>
      {msg && <p>{msg}</p>}
      <h3>Available Reports</h3>
      <ul>
        {reports.map(r => (
          <li key={r.id}>
            {r.course_name} - {r.topic_taught} ({r.date_of_lecture})
          </li>
        ))}
      </ul>
      <h3>Submit Rating</h3>
      <form onSubmit={submitRating}>
        <select
          name="reportId"
          className="form-select mb-2"
          onChange={handleChange}
        >
          <option value="">Select Report</option>
          {reports.map(r => (
            <option key={r.id} value={r.id}>
              {r.course_name} ({r.id})
            </option>
          ))}
        </select>
        <input
          type="number"
          name="rating"
          min="1"
          max="5"
          defaultValue="5"
          onChange={handleChange}
          className="form-control mb-2"
        />
        <textarea
          name="feedback"
          placeholder="Feedback"
          onChange={handleChange}
          className="form-control mb-2"
        />
        <button className="btn btn-primary">Submit Rating</button>
      </form>
    </div>
  );
}
