import React, { useEffect, useState } from "react";

export default function PRL() {
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [msg, setMsg] = useState("");

  // Load reports
  useEffect(() => {
    fetch("http://localhost:5000/api/reports")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setReports(data);
        } else {
          console.error("Unexpected response:", data);
          setReports([]);
        }
      })
      .catch((err) => {
        console.error(err);
        setMsg("‚ùå Failed to load reports");
      });
  }, []);

  const handleChange = (id, value) =>
    setFeedback({ ...feedback, [id]: value });

  const submitFeedback = async (id) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/reports/${id}/feedback`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ feedback: feedback[id] }),
        }
      );
      if (res.ok) {
        setMsg("‚úÖ Feedback saved");
      } else {
        setMsg("‚ùå Failed to save feedback");
      }
    } catch (err) {
      setMsg("‚ùå Error: " + err.message);
    }
  };

  return (
    <div>
      <h2 className="mb-4">üìä PRL Dashboard</h2>
      {msg && <div className="alert alert-info">{msg}</div>}

      {reports.length === 0 ? (
        <p>No reports available</p>
      ) : (
        <div className="row">
          {reports.map((r) => (
            <div key={r.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">{r.course_name}</h5>
                  <h6 className="card-subtitle mb-2 text-muted">
                    {r.course_code} | {r.date_of_lecture}
                  </h6>
                  <p className="card-text">
                    <strong>Lecturer:</strong> {r.lecturer_name} <br />
                    <strong>Topic:</strong> {r.topic_taught}
                  </p>

                  <label className="form-label">Feedback</label>
                  <textarea
                    className="form-control mb-2"
                    value={feedback[r.id] || ""}
                    onChange={(e) => handleChange(r.id, e.target.value)}
                  />
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => submitFeedback(r.id)}
                  >
                    Save Feedback
                  </button>

                  {r.prl_feedback && (
                    <p className="mt-2 text-muted">
                      Existing feedback: {r.prl_feedback}
                    </p>
                  )}
                </div>
                <div className="card-footer text-end">
                  <small className="text-muted">
                    Week {r.week_of_reporting}
                  </small>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
