import React, { useEffect, useState } from "react";

export default function PL() {
  const [courses, setCourses] = useState([]);
  const [reports, setReports] = useState([]);
  const [form, setForm] = useState({ name: "", code: "", lecturerId: "" });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/courses")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setCourses(data);
        } else {
          console.error("Unexpected response:", data);
          setCourses([]);
        }
      })
      .catch(err => setMsg("❌ Failed to load courses"));

    fetch("http://localhost:5000/api/reports")
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setReports(data);
        } else {
          console.error("Unexpected response:", data);
          setReports([]);
        }
      })
      .catch(err => setMsg("❌ Failed to load reports"));
  }, []);

  const handleChange = e =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const addCourse = async e => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (res.ok) {
        setMsg("✅ Course added");
        setCourses([...courses, form]); // optimistic update
      } else {
        setMsg("❌ Failed to add course");
      }
    } catch (err) {
      setMsg("❌ Error: " + err.message);
    }
  };

  return (
    <div>
      <h2>Program Leader (PL) Dashboard</h2>
      {msg && <p>{msg}</p>}

      <h3>Add Course</h3>
      <form onSubmit={addCourse} className="mb-3">
        <input
          name="name"
          placeholder="Course Name"
          className="form-control mb-2"
          onChange={handleChange}
        />
        <input
          name="code"
          placeholder="Course Code"
          className="form-control mb-2"
          onChange={handleChange}
        />
        <input
          name="lecturerId"
          placeholder="Lecturer ID (optional)"
          className="form-control mb-2"
          onChange={handleChange}
        />
        <button className="btn btn-primary">Add Course</button>
      </form>

      <h3>Courses</h3>
      {courses.length === 0 ? (
        <p>No courses available</p>
      ) : (
        <ul>
          {courses.map((c, i) => (
            <li key={i}>
              {c.name} ({c.code})
            </li>
          ))}
        </ul>
      )}

      <h3>Reports Overview</h3>
      {reports.length === 0 ? (
        <p>No reports available</p>
      ) : (
        <ul>
          {reports.map(r => (
            <li key={r.id}>
              {r.course_name} - {r.topic_taught}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
