import React, { useEffect, useState } from "react";

export default function Student() {
  const [reports, setReports] = useState([]);
  const [msg, setMsg] = useState("");
  const [ratings, setRatings] = useState({});
  const [feedback, setFeedback] = useState({});
  const [selectedReport, setSelectedReport] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5001/api/reports")
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setReports(data);
        } else {
          console.error("Unexpected response:", data);
          setReports([]);
        }
      })
      .catch((err) => {
        console.error(err);
        setMsg("❌ Failed to load reports");
      });
  }, []);

  const handleRating = (reportId, rating) => {
    setRatings(prev => ({
      ...prev,
      [reportId]: rating
    }));
  };

  const handleFeedback = (reportId, text) => {
    setFeedback(prev => ({
      ...prev,
      [reportId]: text
    }));
  };

  const submitRating = (reportId) => {
    const rating = ratings[reportId];
    const feedbackText = feedback[reportId];
    
    if (!rating) {
      alert("Please select a rating before submitting");
      return;
    }

    // In a real app, you would send this to your backend
    console.log(`Rating submitted for report ${reportId}:`, {
      rating,
      feedback: feedbackText
    });

    alert(`Rating submitted!\nRating: ${rating}/5\nFeedback: ${feedbackText || 'No feedback provided'}`);
    
    // Clear the rating and feedback for this report
    setRatings(prev => {
      const newRatings = { ...prev };
      delete newRatings[reportId];
      return newRatings;
    });
    
    setFeedback(prev => {
      const newFeedback = { ...prev };
      delete newFeedback[reportId];
      return newFeedback;
    });
  };

  const calculateAttendancePercentage = (actual, total) => {
    if (!total || total === 0) return 0;
    return Math.round((actual / total) * 100);
  };

  const getAttendanceColor = (percentage) => {
    if (percentage >= 80) return "text-success";
    if (percentage >= 60) return "text-warning";
    return "text-danger";
  };

  const viewReportDetails = (report) => {
    setSelectedReport(report);
  };

  const closeReportDetails = () => {
    setSelectedReport(null);
  };

  return (
    <div>
      <h2>Student Dashboard</h2>
      <p className="text-muted">Monitor lectures and provide ratings/feedback</p>
      
      {msg && <div className="alert alert-danger">{msg}</div>}

      <div className="row">
        {reports.length === 0 ? (
          <div className="col-12">
            <div className="alert alert-info">No reports available</div>
          </div>
        ) : (
          reports.map((report) => (
            <div key={report.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card h-100">
                <div className="card-body">
                  <h5 className="card-title">{report.courseName} ({report.courseCode})</h5>
                  
                  <div className="mb-2">
                    <strong>Lecturer:</strong> {report.lecturerName}
                  </div>
                  
                  <div className="mb-2">
                    <strong>Class:</strong> {report.className}
                  </div>
                  
                  <div className="mb-2">
                    <strong>Date:</strong> {new Date(report.dateOfLecture).toLocaleDateString()}
                  </div>
                  
                  <div className="mb-2">
                    <strong>Attendance:</strong> 
                    <span className={`ms-1 ${getAttendanceColor(
                      calculateAttendancePercentage(report.actualPresent, report.totalRegistered)
                    )}`}>
                      {report.actualPresent}/{report.totalRegistered} 
                      ({calculateAttendancePercentage(report.actualPresent, report.totalRegistered)}%)
                    </span>
                  </div>
                  
                  <div className="mb-3">
                    <strong>Status:</strong> 
                    <span className={`badge ms-1 ${
                      report.status === 'approved' ? 'bg-success' : 
                      report.status === 'rejected' ? 'bg-danger' : 'bg-warning'
                    }`}>
                      {report.status}
                    </span>
                  </div>

                  {/* Rating Section */}
                  <div className="rating-section mb-3">
                    <label className="form-label"><strong>Rate this lecture:</strong></label>
                    <div className="d-flex justify-content-between mb-2">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <button
                          key={star}
                          type="button"
                          className={`btn btn-sm ${
                            ratings[report.id] === star ? 'btn-warning' : 'btn-outline-warning'
                          }`}
                          onClick={() => handleRating(report.id, star)}
                        >
                          {star} ⭐
                        </button>
                      ))}
                    </div>
                    
                    <div className="mb-2">
                      <label className="form-label"><small>Feedback (optional):</small></label>
                      <textarea
                        className="form-control form-control-sm"
                        rows="2"
                        placeholder="Share your thoughts about this lecture..."
                        value={feedback[report.id] || ''}
                        onChange={(e) => handleFeedback(report.id, e.target.value)}
                      />
                    </div>
                    
                    <button
                      className="btn btn-success btn-sm w-100"
                      onClick={() => submitRating(report.id)}
                      disabled={!ratings[report.id]}
                    >
                      Submit Rating
                    </button>
                  </div>

                  <button
                    className="btn btn-outline-primary btn-sm w-100"
                    onClick={() => viewReportDetails(report)}
                  >
                    View Full Details
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Report Details Modal */}
      {selectedReport && (
        <div className="modal show d-block" style={{backgroundColor: 'rgba(0,0,0,0.5)'}}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {selectedReport.courseName} - Full Report
                </h5>
                <button type="button" className="btn-close" onClick={closeReportDetails}></button>
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-md-6">
                    <p><strong>Faculty:</strong> {selectedReport.faculty}</p>
                    <p><strong>Class:</strong> {selectedReport.className}</p>
                    <p><strong>Week:</strong> {selectedReport.weekOfReporting}</p>
                    <p><strong>Date:</strong> {new Date(selectedReport.dateOfLecture).toLocaleDateString()}</p>
                    <p><strong>Course Code:</strong> {selectedReport.courseCode}</p>
                  </div>
                  <div className="col-md-6">
                    <p><strong>Lecturer:</strong> {selectedReport.lecturerName}</p>
                    <p><strong>Venue:</strong> {selectedReport.venue}</p>
                    <p><strong>Time:</strong> {selectedReport.scheduledTime}</p>
                    <p><strong>Attendance:</strong> {selectedReport.actualPresent}/{selectedReport.totalRegistered}</p>
                    <p><strong>Status:</strong> 
                      <span className={`badge ms-1 ${
                        selectedReport.status === 'approved' ? 'bg-success' : 
                        selectedReport.status === 'rejected' ? 'bg-danger' : 'bg-warning'
                      }`}>
                        {selectedReport.status}
                      </span>
                    </p>
                  </div>
                </div>
                
                <div className="mt-3">
                  <p><strong>Topic Taught:</strong></p>
                  <p className="border p-2 rounded">{selectedReport.topicTaught || 'Not specified'}</p>
                </div>
                
                <div className="mt-3">
                  <p><strong>Learning Outcomes:</strong></p>
                  <p className="border p-2 rounded">{selectedReport.learningOutcomes || 'Not specified'}</p>
                </div>
                
                <div className="mt-3">
                  <p><strong>Lecturer's Recommendations:</strong></p>
                  <p className="border p-2 rounded">{selectedReport.recommendations || 'Not specified'}</p>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={closeReportDetails}>
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
